sources.src/styles/base/_typography.scss:
@use '../variables/variables';
@use 'mixins';


body{
    color:variables.$color-white;
}


html {
    font-family:variables.$font-family;
    @include mixins.fluid-font-size(16px,21px);
	text-size-adjust: none; //disable the text inflation algorithm used on some smartphones and tablets
}

@mixin font-display-1{
    font-family:variables.$font-family;
    font-size:variables.$font-size-xl;
    margin-bottom: 8px;
    font-weight: 600;
}

@mixin font-display-2{
    font-family:variables.$font-family;
    font-size:variables.$font-size-l;
}

@mixin font-display-3{
    font-family:variables.$font-family;
    font-size:variables.$font-size-m;
    font-weight: 600;
}


@mixin font-body{
    font-family:variables.$font-family;
    font-size:variables.$font-size-sm;
    line-height: 1.3; 
    font-weight: 400;
}

@mixin font-link{
    font-family:variables.$font-family;
    font-size:variables.$font-size-sm;
    text-decoration: none;
    margin:4px;
    color:whitesmoke;
    font-weight: 600;
}

@mixin font-link--secondary{
    font-family:variables.$font-family;
    font-size:variables.$font-size-sm;
    text-decoration: none;
    margin:4px;
    color: hsla(290, 38%, 56%, 0.67);
    font-weight: 500;
}

@mixin font-secondary{
    font-family:variables.$font-family;
    font-size:variables.$font-size-xs;
    font-weight: 300;
    color:hsla(220, 55%, 45%, 0.67);
}

.highlight{
    text-decoration: lime;
}

